# run grid2grid.x executable ##############################################                                                                                                                                                             
$APRUN $exe/grid2grid.x < g2g.ctl.$model > output.$model
 if [ -s ${mdlname}_${YYYYMMDDHH}.vsdb ] ; then
  cat grid2grid.vsdb >> ${mdlname}_${YYYYMMDDHH}.vsdb
  rm -f grid2grid.vsdb
 else
  mv grid2grid.vsdb ${mdlname}_${YYYYMMDDHH}.vsdb
 fi

